//Classe que estabelece a conexao com a base de dados do Sistemapackage util;import java.sql.*;import java.io.*;import java.util.*;public class DataBaseConnection{    public static Connection connection = null;    //DataBaseConnection Constructor    public DataBaseConnection(){}    //Connection Methods    public static Connection makeConnection()throws SQLException,IOException{        if((connection == null) || connection.isClosed()) {            try(InputStream input = new FileInputStream("E:/!UTA(Pratica)/POO/InteliJ_Projects/BankingSystem/src/resources/database")){                Properties properties = new Properties();                properties.load(input);                String url = properties.getProperty("db_url");                String user = properties.getProperty("db_user");                String password =  properties.getProperty("db_password");                connection = DriverManager.getConnection(url,user,password);            }        }        return connection;    }    public static void closeConnection(){        try{            if(connection != null && !connection.isClosed()){                connection.close();            }        }catch(SQLException error){            error.printStackTrace();        }    }}